const widowImg = new Image();
widowImg.src = 'data:image/jpeg;base64,';

widowImg.addEventListener('load', function() {
    const canvas = document.getElementsByTagName('canvas')[0];
    const ctx = canvas.getContext('2d');

    canvas.width = 780;
    canvas.height = 438;

    ctx.drawImage(widowImg, 0, 0, canvas.width, canvas.height);
    const pixels = ctx.getImageData(0, 0, canvas.width, canvas.height); // Returns array where R G B A is index of groups of 4. So i[0] - red, i[1] - green, i[2] - blue, i[3] - alpha
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    let particles = [];
    const numberOfParticles = 5000;

    let mappedImage = [];
    for (let y = 0; y < canvas.height; y++) {
        let row = [];
        for (let x = 0; x < canvas.width; x++) {
            const red = pixels.data[(y * 4 * pixels.width) + (x * 4)];
            const green = pixels.data[(y * 4 * pixels.width) + (x * 4 + 1)];
            const blue = pixels.data[(y * 4 * pixels.width) + (x * 4 + 2)];
            //const alpha = pixels.data[(y * 4 * pixels.width) + (x * 4 + 3)];
            const brightness = calculateRelativeBrightness(red, green, blue);
            const cell = [
                cellBrightness = brightness,
            ];
            row.push(cell);
        }
        mappedImage.push(row);
    }

    function calculateRelativeBrightness(red, green, blue) {
        return Math.sqrt(
            (red * red) * 0.299 + (green * green) * 0.587 + (blue * blue) * 0.114
        )/100; // How were the coefficients derived?
    }

    class Particle {
        constructor() {
            this.x = Math.random() * canvas.width;
            this.y = 0;
            this.speed = 0;
            this.velocity = Math.random() * 0.5;
            this.size = Math.random() * 1.5 + 1;
            this.position1 = Math.floor(this.y);
            this.position2 = Math.floor(this.x);
        }

        update() {
            this.position1 = Math.floor(this.y);
            this.position2 = Math.floor(this.x);
            this.speed = mappedImage[this.position1][this.position2][0];
            let movement = (2.5 - this.speed) + this.velocity;

            this.y += movement;
            if (this.y >= canvas.height) {
                this.y = 0;
                this.x = Math.random() * canvas.width;
            }
        }

        draw() {
            ctx.beginPath();
            ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
            ctx.fillStyle = '#fff';
            ctx.fill();
            ctx.closePath();
        }
    }

    function init() {
        for (let i = 0; i < numberOfParticles; i++) {
            particles.push(new Particle);
        }
    }
    init();
    function animate() {
        ctx.globalAlpha = 0.05;
        ctx.fillStyle = `rgb(0,0,0)`;
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.globalAlpha = 0.2;

        for (let i = 0; i < particles.length; i++) {
            particles[i].update();
            ctx.globalAlpha = particles[i].speed * 0.5;
            particles[i].draw();
        }
        requestAnimationFrame(animate);
    }
    animate();
});